import styled from "styled-components";
import {FaAngleLeft, FaAngleRight} from "react-icons/fa"
import { useState } from "react";

function PostImgBox() {
const imgUrls = [
  "https://lh3.googleusercontent.com/ApelJFrWCjFUvhKeSseI5bnxI-5eDZqq9p0qQ5d-Eiuigdr4ccdFp6I1OnaIpDmoUd6MVplvrzJGNfrLhwvr7PyHOxg-aL3r8A=w1200-h630-rj-pp-nu-e365",
"https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSahT4jtl3duOQHhCvF1lhjJQIyc48aRUABwQ&usqp=CAU",
"https://lh3.googleusercontent.com/ApelJFrWCjFUvhKeSseI5bnxI-5eDZqq9p0qQ5d-Eiuigdr4ccdFp6I1OnaIpDmoUd6MVplvrzJGNfrLhwvr7PyHOxg-aL3r8A=w1200-h630-rj-pp-nu-e365",


]
const [idx,setIdx] = useState(0);

const handleIdx = (operator)=>{
  if(operator === -1){
  if(idx + operator > 0 ) 
  setIdx(idx + operator);

}else {
  if(idx + operator < imgUrls.length -1)
  setIdx(idx + operator);

}
}
  return (
    <Block>

        <ImgList idx={idx} >

          {imgUrls.map((url)=>(<li key={idx}><img src={url} alt="as"></img></li>))}
      </ImgList>
      <Btn onClick={()=> handleIdx(-1)}>

      <FaAngleLeft color="rbga(255,255,255,0.3"/>
      </Btn>
      <Btn onClick={()=> handleIdx(1)}>
      <FaAngleRight color="rbga(255,255,255,0.3" />

      </Btn>
    </Block>
  );
}




const Block = styled.div`
  height: 200px;
  overflow: hidden;
  position: relative;
`;

const ImgList = styled.ul`
  display: flex;
  height: 100%;
  transition: transform 0.25s;
  transform: translate(${({idx})=> idx*-200}px);
  li {
    width: 200px;

    img {
      height: 200%;
    }
  }
`;


const Btn = styled.button`
display: flex;
justify-content: center;
align-items: center;

width: 30px;
height: 30px;
  border: none;
  border-radius: 50%;
  outline: none;

  background-color: transparent;
  position: absolute;
  top: 50%;
  transform: translateY(-50%);

  cursor: pointer;

&:hover{
  background-color: rgba(0,0,0,0.2);

}


&:nth-of-type(1){
  left: 5px;

}

&:nth-of-type(2){
  left: 5px;

}
`;
export default PostImgBox;
