import styled from "styled-components";


function PostImgBox() {
const imgUrls = ["https://lh3.googleusercontent.com/ApelJFrWCjFUvhKeSseI5bnxI-5eDZqq9p0qQ5d-Eiuigdr4ccdFp6I1OnaIpDmoUd6MVplvrzJGNfrLhwvr7PyHOxg-aL3r8A=w1200-h630-rj-pp-nu-e365",
"https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSahT4jtl3duOQHhCvF1lhjJQIyc48aRUABwQ&usqp=CAU"

]
const idx = 0;
  return (
    <Block>

        <ImgList idx={idx} >

          {imgUrls.map((url)=>(<li><img src={url} alt="as"></img></li>))}
      </ImgList>
      <Btn></Btn>
      <Btn></Btn>
    </Block>
  );
}




const Block = styled.div`
  height: 200px;
  overflow: hidden;
  position: relative;
`;

const ImgList = styled.ul`
  display: flex;
  height: 100%;
transform: translate(${({idx})=> idx*-200}px);
  li {
    width: 200px;

    img {
      height: 200%;
    }
  }
`;


const Btn = styled.button`
width: 30px;
height: 30px;
  border: none;
  border-radius: 50%;
  outline: none;

  background-color: rgba(0,0,0,0.2);
  position: 50%;
  top: 50%;
  transform: translateY(-50%);

`;
export default PostImgBox;
